        [ 
                'book_name' => 'Tuổi 20 - Những Năm Tháng Quyết Định Cuộc Đời Bạn', 
                'description' => html_entity_decode('&lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;color: #ff6600; font-size: medium;&quot;&gt;&lt;strong&gt;Tuổi 20 - Những Năm Th&aacute;ng Quyết Định Cuộc Đời Bạn&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Lu&ocirc;n chạy theo đám đ&ocirc;ng&nbsp;Kh&ocirc;ng ti&ecirc;́p sức, c&ocirc;̉ vũ những người xung quanh&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;&lt;span style=&quot;font-size: 12px;&quot;&gt;Nghĩ rằng kh&ocirc;ng phải học hỏi và trưởng thành nữa&lt;/span&gt;&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Nghĩ rằng giúp đỡ ai đó r&ocirc;̀i họ sẽ có ngày trả ơn mình&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Đ&acirc;̀u tư vào những m&ocirc;́i quan h&ecirc;̣ kh&ocirc;ng có giá trị đúng đắn&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Cặp kè với những người bạn ăn chơi l&ecirc;u l&ocirc;̉ng và gi&ecirc;́t thời gian&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Lu&ocirc;n ti&ecirc;u đ&ecirc;́n đ&ocirc;̀ng cu&ocirc;́i cùng mình có được mà kh&ocirc;ng chịu đ&ecirc;̉ dành&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Hẹn hò m&ocirc;̣t người chẳng ra gì&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Qu&ecirc;n rằng lu&acirc;̣t nh&acirc;n quả là có th&acirc;̣t trong cu&ocirc;̣c s&ocirc;́ng&lt;/p&gt;  &lt;p style=&quot;text-align: justify;&quot;&gt;Tuổi thanh xu&acirc;n giống như một cơn mưa r&agrave;o m&ugrave;a hạ chợt đến v&agrave; rồi chợt đi l&uacute;c n&agrave;o kh&ocirc;ng hay. Đừng bỏ ph&iacute; những năm th&aacute;ng quyết định cuộc đời m&igrave;nh c&aacute;c bạn nh&eacute;!&lt;/p&gt;'), 
                'publish_date' => '2014-11-14', 
                'author_id' => rand(1, 20),//225, 
                'company_id' => rand(1, 15),//9, 
                'publishing_house' => 'NXB Lao Động Xã Hội',
